mapper.select:
select user_id, passage_id, SUM(submit_count) AS submit_count, SUM(bill_count) AS bill_count, SUM(unknown_count) AS unknown_count, SUM(success_count) AS success_count, SUM(submit_failed_count) AS submit_failed_count, SUM(other_count) AS other_count from sms_submit_hour_report where hour_time >= #{startTime} and hour_time <= #{endTime}
and user_id = #{userId}
group by user_id, passage_id

select user_id, SUM(submit_count) AS submit_count, SUM(bill_count) AS bill_count, SUM(unknown_count) AS unknown_count, SUM(success_count) AS success_count, SUM(submit_failed_count) AS submit_failed_count, SUM(other_count) AS other_count from sms_submit_hour_report where hour_time >= #{startTime} and hour_time <= #{endTime}
and user_id = #{userId}
group by user_id

select passage_id, SUM(submit_count) AS submit_count, SUM(bill_count) AS bill_count, SUM(unknown_count) AS unknown_count, SUM(success_count) AS success_count, SUM(submit_failed_count) AS submit_failed_count, SUM(other_count) AS other_count from sms_submit_hour_report where hour_time >= #{startTime} and hour_time <= #{endTime}
and passage_id = #{passageId}
group by passage_id

select province_code, SUM(submit_count) AS submit_count, SUM(bill_count) AS bill_count, SUM(unknown_count) AS unknown_count, SUM(success_count) AS success_count, SUM(submit_failed_count) AS submit_failed_count, SUM(other_count) AS other_count from sms_submit_hour_report where hour_time >= #{startTime} and hour_time <= #{endTime}
and passage_id = #{passageId}
group by province_code
